module.exports = async function(data) {
  const products = data.get_all_products;
  const movements = data.get_product_movements;
  const snapshot_event_id = data.create_snapshot_event[0];

  // Calculate stock for each product
  const stockData = [];

  for (const product of products) {
    let quantity = 0;

    // Sum all movements for this product
    const productMovements = movements.filter(m => m.product_id === product.id);

    for (const movement of productMovements) {
      if (movement.type === 'IN') {
        quantity += Number(movement.quantity);
      } else if (movement.type === 'OUT') {
        quantity -= Number(movement.quantity);
      }
    }

    stockData.push({
      snapshot_event_id: snapshot_event_id,
      product_id: product.id,
      quantity: quantity,
      unit_price_eur: product.unit_price_eur
    });
  }

  return {
    stock_data: stockData,
    snapshot_count: stockData.length,
    snapshot_event_id: snapshot_event_id,
    trigger_source: data.snapshot_info.trigger_source
  };
};

module.exports = async function(data) {
  const stock_quantity = data.get_stock_quantity.data.stock_quantity;
  const requested_qty = parseFloat(data.$trigger.payload.quantity) || 0;

  if (stock_quantity < requested_qty) {
    throw new Error(
      `Insufficient stock: only ${stock_quantity} available, tried to remove ${requested_qty}`
    );
  }

  return {
    status: 'success',
    data: {
      stock_quantity
    }
  };
};

