module.exports = async function(data) {
  const product_movements = data.fetch_movement_of_product;

  const stock_quantity = product_movements.reduce((accumulator, movement) => {
    const qty = parseFloat(movement.quantity) || 0;
    return accumulator + (movement.type === 'IN' ? qty : -qty);
  }, 0);
  return {
    status: 'success',
    data: {
      stock_quantity
    }
  };
};

