module.exports = async function (data) {
  const payload = data.$trigger.payload;
  const keys = data.$trigger.keys;

  const movementId = keys?.[0];
  if (!movementId) throw new Error('Missing movement ID');

  // Get current movement data
  const currentMovement = data.get_old_movement_data[0];
  if (!currentMovement) throw new Error('Could not find existing movement data');

  // Get new values or use existing ones
  const newType = payload.type ?? currentMovement.type;
  const newQuantity = parseFloat(payload.quantity ?? currentMovement.quantity);
  const productId = payload.product_id ?? currentMovement.product_id;

  // Validation checks
  if (!newType || !['IN', 'OUT'].includes(newType)) {
    throw new Error('Invalid or missing movement type');
  }

  if (!productId) {
    throw new Error('Missing product_id');
  }

  if (isNaN(newQuantity) || newQuantity <= 0) {
    throw new Error('Invalid quantity: must be a positive number');
  }

  // Get current stock quantity
  const currentStockQuantity = parseFloat(data.get_stock_quantity?.data?.stock_quantity || 0);

  // Calculate the impact of the current movement on stock (before changes)
  let adjustedStock = currentStockQuantity;

  // If editing an existing movement, first reverse its effect on the stock calculation
  if (currentMovement.type === 'IN') {
    adjustedStock -= parseFloat(currentMovement.quantity);
  } else if (currentMovement.type === 'OUT') {
    adjustedStock += parseFloat(currentMovement.quantity);
  }

  // Now calculate what would happen after the change
  let finalStock = adjustedStock;

  // Apply the new movement effect
  if (newType === 'IN') {
    finalStock += newQuantity;
  } else if (newType === 'OUT') {
    finalStock -= newQuantity;
  }

  // Check if the operation would result in negative stock
  if (finalStock < 0) {
    throw new Error(`Operation would result in negative stock: ${adjustedStock} available before this movement, would become ${finalStock} after applying ${newType} of ${newQuantity}`);
  }

  return {
    status: 'success',
    data: {
      product_id: productId,
      movement_id: movementId,
      stock_before_change: currentStockQuantity,
      adjusted_stock: adjustedStock,
      stock_after_change: finalStock,
      old_type: currentMovement.type,
      old_quantity: parseFloat(currentMovement.quantity),
      new_type: newType,
      new_quantity: newQuantity
    }
  };
};

